import { Button, Nav, NavRow, Row, Col, Card, Form } from 'react-bootstrap';
import { connect } from 'react-redux';
import { Link, useHistory, useLocation, NavLink } from 'react-router-dom';
import { useState } from 'react';
import { fetch_user, fetch_single_wellness, fetch_health_vote} from '../api';
import Carousel from 'react-bootstrap/Carousel';
import store from '../store';

function WellnessForm({session, current_wellness, current_user, health_vote}) {
  function photo_path(hash) {
    return "http://localhost:4000/photos/" + hash;
  }

  let location = useLocation().pathname
  let health_id = location.split("/wellness/view/")[1]
  if (typeof(current_wellness.id) == "undefined" || current_wellness.id != health_id) {
    fetch_single_wellness(health_id)
  }

  if(typeof(current_user.id) == "undefined" || current_user.id != current_wellness.user_id) {
    fetch_user(current_wellness.user_id)
    fetch_health_vote(current_wellness.id)
  }

  function LogoutView() {
    let link = "/users/view/" + current_user.id
    return (
      <div>
      <EditButton />
      <h1 className="mt-3">{current_wellness.title}</h1>
      <h2 className="h4 text-secondary">Posted by: {current_user.name}</h2>
      <img className="mt-2 my-4" src={photo_path(current_wellness.photo_hash)}></img>
      <p className="h4">{current_wellness.body}</p>
      </div>
    )
  }

  function EditButton() {
    if (session && session.user_id == current_user.id) {
        let link = "/wellness/edit/" + current_wellness.id
        return (
          <div>
          <Link to={link}  className="mr-2 text-light btn btn-lg btn-info font-weight-bold">Edit</Link>
          <Link to="/wellness/list"  className="my-3 text-light btn btn-lg btn-danger font-weight-bold">Back</Link>
          </div>
        )
    }
    else {
      return (
        <div>
        <Link to="/wellness/list"  className="my-3 text-light btn btn-lg btn-danger font-weight-bold">Back</Link>
        </div>)
    }
  }
  const [vote, setVote] = useState({
    like: health_vote.like, dislike: health_vote.dislike
  });

  function update(field, ev) {
    let u1 = Object.assign({}, vote);
    u1[field] = ev.target.value;
    setVote(u1);
  }

  function LikeDislike() {
    return (
      <Form inline>
      <Form.Group>
        <Form.Label>Like: </Form.Label>
        <Button onClick={(ev) => update(field, ev)}></Button>
      </Form.Group>
      <Form.Group>
        <Form.Label>Dislike: </Form.Label>
      </Form.Group>
      </Form>
    )
  }
  function LoginView() {
    let link = "/users/view/" + current_user.id
    return (
      <div>
      <EditButton />
      <h1 className="mt-3">{current_wellness.title}</h1>
      <h2 className="h4 text-secondary">Posted by: {current_user.name}</h2>
      <img className="mt-2 my-4" src={photo_path(current_wellness.photo_hash)}></img>
      <p className="h4">{current_wellness.body}</p>
      <LikeDislike/>
      </div>
    )
  }

  function WVF({session, current_wellness, current_user, health_vote}) {
    if (session) {
      return <LoginView/>
    }
    else {
      return <LogoutView />
    }
  }

  return(
      <WVF session={session} current_user={current_user} current_wellness={current_wellness} health_vote={health_vote}/>
    )
  }


const WellnessView = connect(
    ({session, current_wellness, current_user, health_vote}) => ({session, current_wellness, current_user, health_vote}))(WellnessForm);

function state2props(_state) {
  return {};
}

export default connect(state2props)(WellnessView);
