import { Row, Col, Form, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { useState } from 'react';
import { useHistory } from 'react-router-dom';
import pick from 'lodash/pick';
import store from '../store';
import { create_user, fetch_users, fetch_user, api_login, fetch_reason } from '../api';

function UsersView() {
  let history = useHistory();

  function update(field, ev) {
    let u1 = Object.assign({}, user);
    u1[field] = ev.target.value;
    u1.password = u1.pass1;
    u1.pass_msg = check_pass(u1.pass1, u1.pass2);
    setUser(u1);
  }

  function updatePhoto(ev) {
    let p1 = Object.assign({}, user);
    p1["photo"] = ev.target.files[0];
    setUser(p1);
  }

  return (
    <Form>
      <Form.Group>
        <h1 className="mt-5">My Profile</h1>
        <Form.Label>Name</Form.Label>
        <Form.Control type="text"
                      onChange={
                        (ev) => update("name", ev)}
          value={user.name} />
      </Form.Group>
      <Form.Group>
        <Form.Label>Email</Form.Label>
        <Form.Control type="text"
                      onChange={
                        (ev) => update("email", ev)}
          value={user.email} />
      </Form.Group>

      <Form.Group controlId="exampleForm.ControlSelect1">
        <Form.Label>Reason For Using This Website</Form.Label>
        <Form.Control as="select" onChange={(ev) => update("reason", ev)} value={user.reason}>
          <option></option>
          <option>Cat Wellness</option>
          <option>Breeder/Adoption</option>
          <option>Lost/Found Cat</option>
          <option>Food Choices/Recommendations</option>
          <option>Other</option>
        </Form.Control>
      </Form.Group>
      <Form.Group>
            <Form.Label>Profile Picture</Form.Label>
            <Form.Control type="file" onChange={updatePhoto} />
      </Form.Group>
      <Form.Group>
        <Form.Label>Password</Form.Label>
        <Form.Control type="password"
          onChange={
            (ev) => update("pass1", ev)}
          value={user.pass1} />
        <p>{user.pass_msg}</p>
      </Form.Group>
      <Form.Group>
        <Form.Label>Confirm Password</Form.Label>
        <Form.Control type="password"
          onChange={
            (ev) => update("pass2", ev)}
          value={user.pass2} />
      </Form.Group>
    </Form>
  );
}

function state2props(_state) {
  return {};
}

export default connect(state2props)(UsersView);
