import { Button, Nav, NavRow, Row, Col, Card, Form } from 'react-bootstrap';
import { connect } from 'react-redux';
import { Link, useHistory, useLocation, NavLink } from 'react-router-dom';
import { useState } from 'react';
import { fetch_user, fetch_single_wellness, update_wellness_vote } from '../api';
import Carousel from 'react-bootstrap/Carousel';
import store from '../store';

function WellnessForm({session, current_wellness, score}) {
  function photo_path(hash) {
    return "http://localhost:4000/photos/" + hash;
  }

  let location = useLocation().pathname
  let history = useHistory()
  let health_id = location.split("/wellness/view/")[1]
  if (typeof(current_wellness.id) == "undefined" || current_wellness.id != health_id) {
    fetch_single_wellness(health_id)
  }

  function LogoutView() {
    let link = "/users/view/" + current_wellness.user.id
    return (
      <div>
      <EditButton />
      <h1 className="mt-3">{current_wellness.title}</h1>
      <h2 className="h4 text-secondary">Posted by: {current_wellness.user.name}</h2>
      <img className="mt-2 my-4" src={photo_path(current_wellness.photo_hash)}></img>
      <p className="h4">{current_wellness.body}</p>
      </div>
    )
  }

  function EditButton() {
    if (session && session.user_id == current_wellness.user.id) {
        let link = "/wellness/edit/" + current_wellness.id
        return (
          <div>
          <Link to={link}  className="mr-2 text-light btn btn-lg btn-info font-weight-bold">Edit</Link>
          <Link to="/wellness/list"  className="my-3 text-light btn btn-lg btn-danger font-weight-bold">Back</Link>
          </div>
        )
    }
    else {
      return (
        <div>
        <Link to="/wellness/list"  className="my-3 text-light btn btn-lg btn-danger font-weight-bold">Back</Link>
        </div>)
    }
  }

  function WVF({session, current_wellness, score}) {
    function updateScore() {
      if(current_wellness.votes.includes(session.user_id)) {
        update_wellness_vote(current_wellness, session.user_id, false)
      }
      else {
        update_wellness_vote(current_wellness, session.user_id, true)
      }
    }

    if (session) {
      let link = "/users/view/" + current_user.id
      return (
        <div>
        <EditButton />
        <h1 className="mt-3">{current_wellness.title}</h1>
        <h2 className="h4 text-secondary">Posted by: {current_user.name}</h2>
        <img className="mt-2 my-4" src={photo_path(current_wellness.photo_hash)}></img>
        <p className="h4">{current_wellness.body}</p>
        <Form>
          <Form.Group>
            <Button className="btn-lg btn-light text-danger font-weight-bold" onClick={updateScore}>{score} ðŸ’—</Button>
          </Form.Group>
        </Form>
        </div>
      )
    }
    else {
      let link = "/users/view/" + current_wellness.user.id
      return (
        <div>
        <EditButton />
        <h1 className="mt-3">{current_wellness.title}</h1>
        <h2 className="h4 text-secondary">Posted by: {current_wellness.user.name}</h2>
        <img className="mt-2 my-4" src={photo_path(current_wellness.photo_hash)}></img>
        <p className="h4">{current_wellness.body}</p>
        </div>
      )
    }
  }

  return(
      <WVF session={session} current_wellness={current_wellness} score={score}/>
    )
  }


const WellnessView = connect(
    ({session, current_wellness, score}) => ({session, current_wellness, score}))(WellnessForm);

function state2props(_state) {
  return {};
}

export default connect(state2props)(WellnessView);
