import { Nav, NavRow, Row, Col, Card } from 'react-bootstrap';
import { connect } from 'react-redux';
import { Link, useHistory, useLocation, NavLink } from 'react-router-dom';
import { fetch_user, fetch_single_wellness } from '../api';
import Carousel from 'react-bootstrap/Carousel';
import store from '../store';

function WellnessForm({session, current_wellness, current_user}) {
  let location = useLocation().pathname
  let health_id = location.split("/wellness/view/")[1]
  if (typeof(current_wellness.id) == "undefined" || current_wellness.id != health_id) {
    fetch_single_wellness(health_id)
  }

  if(typeof(current_user.id) == "undefined" || current_user.id != current_wellness.user_id) {
    fetch_user(current_wellness.user_id)
  }

  function LogoutView() {
    let link = "/users/view/" + current_user.id
    return (
      <div>
      <h1 className="mt-3">{current_wellness.title}</h1>
      <h2 className="h4 text-secondary">Posted by: {current_user.name}</h2>
      </div>
    )
  }

  function LoginView() {
    return (
      <div>
      <h1 className="mt-3">{current_wellness.title}</h1>
      <h2 className="h4 text-secondary">Posted by: {current_user.name}</h2>
      </div>
    )
  }

  function WVF({session, current_wellness, current_user}) {
    if (session) {
      return <LoginView/>
    }
    else {
      return <LogoutView />
    }
  }

  return(
      <WVF session={session} current_user={current_user} current_wellness={current_wellness}/>
    )
  }


const WellnessView = connect(
    ({session, current_wellness, current_user}) => ({session, current_wellness, current_user}))(WellnessForm);

function state2props(_state) {
  return {};
}

export default connect(state2props)(WellnessView);
