import { Button, Form } from 'react-bootstrap';
import { connect } from 'react-redux';
import { useHistory, useLocation} from 'react-router-dom';
import { useState } from 'react';
import pick from 'lodash/pick';
import { fetch_adoption, fetch_adoption_search } from '../api';
import store from '../store';

function FoodList({adoption, cat_breed }) {
  const [engine, setEngine] = useState({

  });

  function onSubmit(ev) {
    ev.preventDefault();
  }

  function update(field, ev) {
    let u1 = Object.assign({}, engine);
    u1[field] = ev.target.value;
    setEngine(u1);
  }

  function ListingBreed() {
    let rows = cat_breed.map((breed) => {
      const link = "view/" + food.id
      const image_link = photo_path(food.photo_hash)
      return (
        <Card>
          <Card.Img variant="top" src={image_link} />
          <Card.Body>
            <Card.Title>{food.brand}</Card.Title>
            <Card.Text>{food.body}</Card.Text>
            <Link to={link}>Check For More Info</Link>
          </Card.Body>
        </Card>
      )})
      return
      <Form.Control as="select" onChange={(ev) => update("type", ev)} value={food.type}>
      {rows}</Form.Control>
    }
  return(
    <div>
      <p className="display-1">Adoption Center</p>
      <Form onSubmit={onSubmit}>
        <Form.Group controlId="exampleForm.ControlSelect1">
          <Form.Label>Product Type</Form.Label>
          <ListingBreed />
      </Form.Group>
      </Form>
    </div>
  )
}

function state2props({adoption, cat_breed}) {
  return {adoption, cat_breed };
}

export default connect(state2props)(FoodList);
