import { Nav, NavRow, Row, Col, Form, Button } from 'react-bootstrap';
import { connect } from 'react-redux';
import { useState } from 'react';
import { useHistory, NavLink } from 'react-router-dom';
import pick from 'lodash/pick';
import store from '../store';
import { api_login, fetch_reason } from '../api';

function UsersView() {
  let history = useHistory();

  function photo_path(hash) {
    return "http://localhost:4000/photos/" + hash;
  }

  function Redirect({to, children}) {
    return (
      <Nav.Item>
        <NavLink to={to} exact
          className="btn btn-lg font-weight-bold text-light btn-info"
          activeClassName="active">
          {children}
        </NavLink>
      </Nav.Item>
    );
  }

  function View({session}) {
        return(
        <Row>
          <Col>
            <h1 className="mt-3 my-3">My Profile</h1>
            <Redirect to="/users/edit">Edit Profile</Redirect>
            <img className="my-4" src={photo_path(session.photo_hash)} width="200px" height="200px"></img>
            <h3>Name: {session.name}</h3>
            <h3>Email: {session.email}</h3>
            <h3>Reason: {session.reason}</h3>
          </Col>
        </Row>
      )
  }

  function LoginView({session}) {
    if (session) {
      return <View session={session}/>
    }
    else {
      return <h1 className="mt-5 ml-5">Please Login In First</h1>
    }
  }
  const LoginInRegister = connect(
    ({session}) => ({session}))(LoginView);

  return (
    <div>
      <LoginInRegister />
    </div>
  );
}

function state2props(_state) {
  return {};
}

export default connect(state2props)(UsersView);
