import { createStore, combineReducers } from 'redux';
//referenced the fetch Structure that store the value of the web from lecture
//code SPA Structure from Nat Tuck CS4550 Northeastern University 
function wellness(state= [], action ) {
  switch (action.type) {
    case 'wellness/set':
      return action.data;
    default:
      return state;
  }
}

function score(state=0, action) {
  switch (action.type) {
    case 'score/set':
      return action.data;
    default:
      return state;
  }
}

function users(state = [], action) {
  switch (action.type) {
    case 'users/set':
      return action.data;
    default:
      return state;
  }
}

function user_form(state = {}, action) {
  switch (action.type) {
    case 'user_form/set':
      return action.data;
    default:
      return state;
  }
}


function comments(state=[], action) {
  switch (action.type) {
    case 'comments/set':
      return action.data;
    default:
      return state;
  }
}

function forumcomments(state=[], action) {
  switch (action.type) {
    case 'forumcomments/set':
      return action.data;
    default:
      return state;
  }
}

function wellness_form(state = {}, action) {
  switch (action.type) {
    case 'wellness_form/set':
      return action.data;
    default:
      return state;
  }
}

function session(state = load_session(), action) {
  switch (action.type) {
    case 'session/set':
      save_session(action.data);
      return action.data;
    case 'session/clear':
      delete_session();
      return null;
    default:
      return state;
  }
}

function save_session(sess) {
  let session = Object.assign({}, sess, {time: Date.now()});
  localStorage.setItem("session", JSON.stringify(session));
}

function delete_session() {
  localStorage.removeItem("session");
}

function load_session() {
  let session = localStorage.getItem("session");
  if (!session) {
    return null;
  }
  session = JSON.parse(session);
  let age = Date.now() - session.time;
  let hours = 60*60*1000;
  if (age < 24 * hours) {
    return session;
  }
  else {
    return null;
  }
}

function error(state = null, action) {
  switch (action.type) {
    case 'error/set':
      return action.data;
    case 'session/set':
      return null;
    default:
      return state;
  }
}

function forums(state=[], action) {
  switch (action.type) {
    case 'forums/set':
      return action.data;
    default:
      return state;
  }
}

function forum_form(state={}, action) {
  switch (action.type) {
    case 'forum_form/set':
      return action.data;
    default:
      return state;
  }
}

function forum_score(state=0, action) {
  switch (action.type) {
    case 'forum_score/set':
      return action.data;
    default:
      return state;
  }
}

function foods(state=[], action) {
  switch (action.type) {
    case 'foods/set':
      return action.data;
    default:
      return state;
    }
}

function food_form(state={}, action) {
  switch (action.type) {
    case 'food_form/set':
      return action.data;
    default:
      return state;
    }
}

function cat_breed(state=[], action) {
  switch (action.type) {
    case 'cat_breed/set':
      return action.data;
    default:
      return state;
    }
}

function food_like(state=0, action) {
  switch (action.type) {
    case 'food_like/set':
      return action.data;
    default:
      return state;
    }
}

function food_dislike(state=0, action) {
  switch (action.type) {
    case 'food_dislike/set':
      return action.data;
    default:
      return state;
    }
}

function adoption(state=[], action) {
  switch (action.type) {
    case 'adoption/set':
      return action.data;
    default:
      return state;
    }
}

function root_reducer(state, action) {
    let reducer = combineReducers({
        adoption, food_dislike, food_like, cat_breed, food_form, foods, forumcomments, forum_score, forum_form, forums, comments, score,
        wellness_form, wellness, users, user_form, session, error,
    });
    return reducer(state, action);
}



let store = createStore(root_reducer);
export default store;
